import React, { Fragment, useState } from 'react'
import styled from 'styled-components'

interface Props {
    children?: React.ReactNode,
    style?: object,
    menuOptions: Array<any> | Object

}

export const Button = styled.button`
color: green;
background-color: red;
border-bottom: 2px solid #daa520;
padding: .5rem;
margin: .5rem;
background: transparent;
font-weight: 800;
border-radius: .7rem;
`

const MenuContainer = styled.div`
display: flex;
flex-direction: column;
justify-content: flex-start;
align-items: center;
position: relative;
overflow: auto;
`

export default function DropdownMenu ({ children, style, menuOptions }: Props) {
  const [menuOpen, toggleMenu] = useState(false)

  return (
    <div style={{ position: 'fixed' }}>
        <Button onClick={() => toggleMenu(!menuOpen)}> {children}
          <MenuContainer>
            {menuOpen
              ? <Fragment>
                {Object.values(menuOptions).map((item, index) => (
                  <Button key={index} onClick={() => toggleMenu(!menuOpen)}>
                    <a href={item.href}>{item.name}</a>
                  </Button>
                ))}
              </Fragment>
              : <Fragment />
            }
          </MenuContainer>
        </Button>
    </div>
  )
}
