import React, { Fragment, useState } from 'react'
import styled from 'styled-components'

interface Props {
    children?: React.ReactNode,
    style?: object,
    menuOptions: Array<any> | Object,

}

export const Button = styled.button`
color: green;
background-color: red;
border-bottom: 2px solid #daa520;
padding: .5rem;
margin: .5rem;
background: transparent;
font-weight: 800;
border-radius: .7rem;
`

const MenuContainer = styled.div`
display: flex;
flex-direction: column;
background: rgb(211,211,211);
justify-content: flex-start;
align-items: center;
position: absolute;
overflow: auto;
height: 10rem;
width: 10rem;
margin: 1rem;
padding: 1rem;
border-radius: .5rem;
`

export default function DropdownMenu ({ children, style, menuOptions }: Props) {
  const [menuOpen, toggleMenu] = useState(false)
  const [renderMenu, toggleRenderMenu] = useState(false)

  // const handleBlur = (e:any) => {
  //   e.preventDefault()
  //   toggleMenu(!menuOpen)
  // }

  const handleClick = (e:any) => {
    e.preventDefault()
    toggleMenu(!menuOpen)
  }

  return (
    <div style={{ position: 'sticky' }}>
        <Button onFocus={() => toggleRenderMenu(true)} // Need to mess with the blur event.  I think it's causing
                onBlur={() => toggleRenderMenu(false)} // the issue I'm having with my inability to follow the a tag.
                > {children}
          <div>
            {renderMenu
              ? <MenuContainer>
                {Object.values(menuOptions).map((item, index) => (
                  <Button key={index} onClick={handleClick} >
                    <a href={item.href} target="_blank" rel="noopener noreferrer">{item.name}</a>
                  </Button>
                ))}
              </MenuContainer>
              : <Fragment />
            }
          </div>
        </Button>
    </div>
  )
}
