import React, { Fragment, useState, useRef } from 'react'
import styled from 'styled-components'

interface Props {
    children?: React.ReactNode,
    style?: object,
    menuOptions: Array<any> | Object,

}

export const Button = styled.button`
color: green;
background-color: red;
border-bottom: 2px solid #daa520;
padding: .5rem;
margin: .5rem;
background: transparent;
font-weight: 800;
border-radius: .7rem;
`
const MenuLink = styled.a`
color: green;
background-color: red;
border-bottom: 2px solid #daa520;
padding: .5rem;
margin: .5rem;
background: transparent;
font-weight: 800;
border-radius: .7rem;
`

const MenuContainer = styled.div`
display: flex;
flex-direction: column;
background: rgb(211,211,211);
justify-content: flex-start;
align-items: center;
position: absolute;
overflow: auto;
height: 10rem;
width: 10rem;
margin: 1rem;
padding: 1rem;
border-radius: .5rem;
`

export default function DropdownMenu ({ children, style, menuOptions }: Props) {
  const [menuOpen, toggleMenu] = useState(false)
  const menuRef = useRef(null)

  // const handleBlur = (e:any) => {
  //   e.preventDefault()
  //   toggleMenu(!menuOpen)
  // }

  const handleClick = (e:any) => {
    e.preventDefault()
    toggleMenu(!menuOpen)
  }

  return (
    <div style={{ position: 'sticky' }} >
            {menuOpen
              ? <Fragment>
                <Button >{children}</Button>
                <MenuContainer>
                  {console.log(menuRef)}
                {Object.values(menuOptions).map((item, index) => (
                    <MenuLink key={index} href={item.href} target="_blank" rel="noopener noreferrer"
                              ref={menuRef} onBlur={() => { if (menuRef !== MenuLink.ref) { toggleMenu(!menuOpen); console.log(menuRef) } }}
                              >{item.name}</MenuLink>
                ))}
              </MenuContainer>
              </Fragment>
              : <Button onFocus={handleClick} // useRef hook may be the key to solving this problem.
              > {children}</Button>
            }
          {/* </div> */}

    </div>
  )
}
